import React, { useState, useEffect } from 'react';
import { Redirect, useParams } from 'react-router-dom';
import Socket, { socket } from '../socket/socket';

export default function Lobby(props) {
    const [newUser, setNewUser] = useState(false);
    const gameId = useParams().gameId;

    useEffect(() => {
        if (props.username) {
            setNewUser(true);
        }
    }, [])

    const newPlayerJoin = () => {
        socket.emit("createGameLobby", {gameId, username: props.username});
        setNewUser(true);
    }
    return (
        <div>
        {
        newUser ? 
            <div>
                <div>
                    Welcome to online chess! Send this link with a friend to start your chess game
                </div>

                <div>
                    <input type="text" value={window.location} disabled />
                </div>

                <div>
                    <p>Waiting for game to start ...</p>
                </div>
            </div>
        :
            <div>
                <p>Enter username</p> <br/>
                <input type="text" onChange={(event) => {props.setUsername(event.target.value)}} /> <br/>
                <button onClick={() => newPlayerJoin()}>Enter</button>
            </div>
        }
        </div>

    )
}
