import Piece from './Piece';

export default class King extends Piece {
    constructor(player) {
        super(player, (player === 1 ? 'whiteurl' : 'blackurl'));
    }

    isMoveValid = (initialPos, endPos) => {
        return (
            initialPos + 1 === endPos || 
            initialPos - 1 === endPos || 
            initialPos + 7 === endPos || 
            initialPos + 8 === endPos || 
            initialPos + 9 === endPos || 
            initialPos - 9 === endPos || 
            initialPos - 8 === endPos || 
            initialPos - 7 === endPos 
        );
    }

    getPathIndicies = (initialPos, endPos) => {
        return[];
    }
}
