import React from 'react';
import Board from './Board';
import initializeChessBoard from '../helpers/InitializeChessBoard';

import '../App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      squares: initializeChessBoard(),
      playerTurn: 1,
      selectedIndex: -1,
    }
  }

  handleClick(index) {
    const squares = this.state.squares.slice();
    console.log(index);
    //piece has not been selected
    if (this.state.selectedIndex < 0) {
      if (squares[index] && (squares[index].player === this.state.playerTurn)) {
        squares[index].style = {...squares[index].style, backgroundColor: "#ffcb5f" };
        this.setState({
          squares: squares,
          selectedIndex: index
        });
      }
      else {
        console.log("That is not your piece");
      }
    }

    //piece has been selected
    else {
      squares[this.state.selectedIndex].style = {...squares[this.state.selectedIndex].style, backgroundColor: null}

      if (squares[index] !== null && squares[index].player === this.state.playerTurn) {
        console.log("That's your own piece wtf");
        this.setState({
          squares: squares, 
          selectedIndex: -1
        })
      }
      else {
        const validMove = squares[this.state.selectedIndex].isMoveValid(this.state.selectedIndex, index);
        const pathIndicies = squares[this.state.selectedIndex].getPathIndicies(this.state.selectedIndex, index);
        if (validMove) {
          var valid = true;
          for (var i = 0; i < pathIndicies.length && valid; ++i) {
            if (squares[i] !== null) {
              valid = false;
            }
          }

          if (valid) {
            squares[i] = squares[this.state.selectedIndex];
            squares[this.state.selectedIndex] = null;
            var nextTurn = this.state.playerTurn === 1 ? 2 : 1;
          }
        }
      }
    }
  }

  render() {
    return (
      <div className="App">
        <Board 
          squares={this.state.squares}
          onClick={(index) => this.handleClick(index)}
        />
      </div>
    );
  }
}

export default App;
