import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';
import {socket} from '../socket/socket';

export default function Join(props) {
    const gameId = uuidv4();

    const generateGameId = () => {
        socket.emit('createGameLobby', gameId);
    };

    return (
        <div>
            {props.username ?
                <Redirect to={`/game/${gameId}`} />
                :
                <div>
                    <h1>Welcome to online chess!</h1>
                    <label>Name: </label>
                    <input type="text" name="name" className="join-input" onChange={(event) => {props.setUsername(event.target.value)}} required /> <br/>
                    <Link onClick={(event) => { props.username.length === 0 ? event.preventDefault() : generateGameId()}} >
                        <button>Enter!</button>
                    </Link>
                </div>

            }

        </div>
    )
}
