    const [message, setMessage] = useState("");
    const [chatLog, setChatLog] = useState({});

    return (
        <div>
            <ul id="chat-log"></ul>
            <form>
            <TextField id="standard-basic" label="Standard" />
            <Button variant="contained" color="primary" onClick={() => newPlayerJoin()}>
                Send
            </Button>
            </form>
        </div>
    )
}
