import React from 'react';
import Board from './Board';
import initializeChessBoard from '../helpers/InitializeChessBoard';

import '../App.css';
import Rook from '../pieces/Rook';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      squares: initializeChessBoard(),
      playerTurn: 1,
      selectedIndex: -1,
      kingInCheck: false,
      counter: 0
    }
  }

  checkValidPath(path) {
    var valid = true;

    for (var i = 0; i < path.length && valid; ++i) {
      if (this.state.squares[path[i]] !== null) {
        valid = false;
      }
    }

    return valid;
  }

  checkRook(squares, index) {
    if (index === 6 || index === 62) {
      if (squares[index + 1].getName() === "Rook" && !squares[index + 1].moved()) {
        return true;
      }
    }

    if (index === 2 || index === 58) {
      if (squares[index - 2].getName() === "Rook" && !squares[index - 2].moved()) {
        return true;
      }
    }
  }

  checkCastle(squares, index) {
    if (this.state.selectedIndex === 4 || this.state.selectedIndex === 60) {
      if (!squares[63].moved() || !squares[56].moved() || !squares[0].moved() || !squares[7].moved()) {
        //short castle
        if (index === 62) {
          if (this.checkRook(squares, index)) {
            if (squares[61] === null && squares[62] === null) {
              squares[index] = squares[this.state.selectedIndex];
              squares[index].handleMoved();
              squares[this.state.selectedIndex] = null;
              squares[61] = new Rook(1);
              squares[63] = null;
            }

            else {
              return false;
            }
          }
        }

        if (index === 6) {
          if (this.checkRook(squares, index)) {
            if (squares[5] === null && squares[6] === null) {
              squares[index] = squares[this.state.selectedIndex];
              squares[index].handleMoved();
              squares[this.state.selectedIndex] = null;
              squares[5] = new Rook(2);
              squares[7] = null;
            }
            else {
              return false;
            }
          }
        }

        //Long castle
        if (index === 2) {
          if (this.checkRook(squares, index)) {
            if (squares[1] === null && squares[2] === null && squares[3] === null) {
              squares[index] = squares[this.state.selectedIndex];
              squares[index].handleMoved();
              squares[this.state.selectedIndex] = null;
              squares[3] = new Rook(2);
              squares[0] = null;
            }
            else {
              return false;
            }
          }
        }

        if (index === 58) {
          if (this.checkRook(squares, index)) {
            if (squares[57] === null && squares[58] === null && squares[59] === null) {
              squares[index] = squares[this.state.selectedIndex];
              squares[index].handleMoved();
              squares[this.state.selectedIndex] = null;
              squares[59] = new Rook(1);
              squares[56] = null;
            }
            else {
              return false;
            }
          }
        }

        return squares;
      }
    }
  }

  handleClick(index) {
    //shallow copy squares 
    const squares = this.state.squares.slice();

    console.log(index);
    //highlight chess piece
    if (this.state.selectedIndex < 0) {
      if (squares[index] && (squares[index].player === this.state.playerTurn)) {
        squares[index].style = {...squares[index].style, backgroundColor: "#575E6B" };
        this.setState({
          squares: squares,
          selectedIndex: index
        });
      }
      else {
        console.log("That is not your piece");
      }
    }

    //piece has been selected
    else {
      squares[this.state.selectedIndex].style = {...squares[this.state.selectedIndex].style, backgroundColor: null};

      console.log(squares[this.state.selectedIndex]);

      // castling king and rook
      if (squares[this.state.selectedIndex].getName() === "King" && !squares[this.state.selectedIndex].moved() && (index === 2 || index === 6 || index === 58 || index === 62)) {
        const newSquares = this.checkCastle(squares, index);
        if (newSquares) {
          var nextTurn = this.state.playerTurn === 1 ? 2 : 1;
          this.setState({
            squares: newSquares,
            selectedIndex: -1,
            playerTurn: nextTurn
          })
        }
        else {
          console.log("invalid move");
          this.setState({
            selectedIndex: -1

          })
        }
      }

      //en passant
      else if (squares[this.state.selectedIndex].getName() === "Pawn" && (squares[index] === this.state.selectedIndex + 7 || squares[index] === this.state.selectedIndex - 7 || squares[index] === this.state.selectedIndex + 9 || squares[index] === this.state.selectedIndex - 9) && squares[index] === null) {
        var leftPawnPos = false;
        var rightPawnPos = false;
        if (squares[this.state.selectedIndex + 1].getName() === "Pawn" && squares[this.state.selectedIndex + 1].player !== this.state.playerTurn) {
          rightPawnPos = this.state.selectedIndex + 1;
        }
        if (squares[this.state.selectedIndex - 1].getName() === "Pawn" && squares[this.state.selectedIndex - 1].player !== this.state.playerTurn) {
          leftPawnPos = this.state.selectedIndex - 1;
        }
        const validPassant = squares[this.state.selectedIndex].isMoveValid(this.state.selectedIndex, index, false, leftPawn, rightPawn);

        if (validPassant) {
          if (index === this.state.selectedIndex - 9 || index === this.state.selectedIndex - 7) {
            squares[index] = squares[this.state.selectedIndex];
            squares[this.state.selectedIndex] = null;
            squares[index + 8] = null;
          }

          else if (index === this.state.selectedIndex + 9 || this.state.selectedIndex + 7) {
            squares[index] = squares[this.state.selectedIndex];
            squares[this.state.selectedIndex] = null;
            squares[index - 8] = null;
          }

        }
      }

      //if destination of piece is clicked on own piece
      else if (squares[index] !== null && squares[index].player === this.state.playerTurn) {
        console.log("That's your own piece wtf");
        this.setState({
          squares: squares, 
          selectedIndex: -1
        })
      }

      // if destination is not on own piece
      else {
        const isSquareOccupied = squares[index] === null ? false : true;
        const validMove = squares[this.state.selectedIndex].isMoveValid(this.state.selectedIndex, index, isSquareOccupied, leftPawn, rightPawn);
        const pathIndicies = squares[this.state.selectedIndex].getPathIndicies(this.state.selectedIndex, index);
        if (validMove) {
          if (this.checkValidPath(pathIndicies)) {
            squares[index] = squares[this.state.selectedIndex];
            squares[index].handleMoved();
            squares[this.state.selectedIndex] = null;
            var nextTurn = this.state.playerTurn === 1 ? 2 : 1;
            this.setState({
              squares: squares,
              playerTurn: nextTurn,
              selectedIndex: -1
            });
          }
          else {
            console.log(pathIndicies);
            this.setState({
              squares: squares, 
              selectedIndex: -1
            });
          }
        }
        else {
          console.log("invalid move");
          this.setState({
            squares: squares, 
            selectedIndex: -1
          });
        }
      }
    }
  }

  render() {
    return (
      <div className="App">
        <Board 
          squares={this.state.squares}
          onClick={(index) => this.handleClick(index)}
        />
      </div>
    );
  }
}

export default App;
