import Piece from './Piece';

export default class Rook extends Piece {
    constructor(player) {
        super(player, player === 2 ? 'https://upload.wikimedia.org/wikipedia/commons/7/72/Chess_rlt45.svg' : 'https://upload.wikimedia.org/wikipedia/commons/f/ff/Chess_rdt45.svg');
        var hasMoved = false;
    }

    isMoveValid = (initialPos, endPos) => {
        const diff = Math.abs(endPos - initialPos);

        return (
            diff % 8 === 0 ||
            diff < 8
        )
    }

    getPathIndicies = (initialPos, endPos) => {
        const diff = Math.abs(endPos - initialPos);
        var increment;
        var indicies = [];

        if (diff % 8 === 0) {
            increment = 8;
        }

        else {
            increment = 1;
        }

        if (endPos > initialPos) {
            for (var i = initialPos + increment; i < endPos; i += increment) {
                indicies.push(i);
            }
        }

        else {
            for (i = initialPos - increment; i > endPos; i -= increment) {
                indicies.push(i);
            }
        }

        return indicies;
    }
}
