import Piece from './Piece';

export default class Bishop extends Piece {
    constructor(player) {
        super(player, player === 1 ? 'whiteUrl' : 'blackUrl');
    }

    isMoveValid = (initialPos, endPos) => {
        const diff = Math.abs(endPos - initialPos);
        return (
            diff % 9 === 0 ||
            diff % 7 === 0
        );
    }

    getPathIndicies = (initialPos, endPos) => {
        var indicies = [];
        var increment;
        const diff = Math.abs(endPos - initialPos);

        if (diff % 9 === 0) {
            increment = 9
        }

        else if (diff % 7 === 0) {
            increment = 7
        }

        if (endPos > initialPos) {
            for (var i = initialPos + increment; i < endPos; i += increment) {
                indicies.push(i);
            }
        }

        else {
            for (var i = initialPos - increment; i > endPos; i -= increment) {
                indicies.push(i);
            }
        }

        return indicies;
    }
}
