import React from 'react';
import Board from './Board';
import initializeChessBoard from '../helpers/InitializeChessBoard';

import '../App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      squares: initializeChessBoard(),
      playerTurn: 1,
    }
  }

  handleClick(index) {
    const squares = this.state.squares.slice();

    if (squares[index] && squares[index].player === this.state.playerTurn) {
      console.log(squares[index]);
      squares[index].style = {...squares[index].style, backgroundColor: "#fff35f" };
      this.setState({
        squares: squares
      })
    }
    else {
      console.log("Not true");
    }
  }

  render() {
    return (
      <div className="App">
        <Board 
          squares={this.state.squares}
          onClick={(index) => this.handleClick(index)}
        />
      </div>
    );
  }
}

export default App;
