import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Join() {
    const [name, setName] = useState("");
    const [room, setRoom] = useState("");
    return (
        <div>
            <div>
                <h1>Welcome to online chess!</h1>
                <label for="name">Name: </label>
                <input type="text" name="name" className="join-input" onChange={(event) => {setName(event.target.value)}} /> <br/>
                <input type="text" name="room" className="join-input" onChange={(event) => {setRoom(event.target.value)}} /> <br/>
                <Link onClick={(event) => (!name || !room ? event.preventDefault() : null )} to={`/game?name=${name}&room=${room}`}>
                    <button>Join!</button>
                </Link>
            </div>
        </div>
    )
}
