import Piece from './Piece';

export default class King extends Piece {
    constructor(player) {
        super(player, (player === 1 ? 'https://upload.wikimedia.org/wikipedia/commons/thumb/e/ed/Western_white_side_Rook.svg/1200px-Western_white_side_Rook.svg.png' : 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/5a/Western_black_side_Rook.svg/1200px-Western_black_side_Rook.svg.png'));
        var hasMoved = false;
    }

    isMoveValid = (initialPos, endPos) => {
        return (
            initialPos + 1 === endPos || 
            initialPos - 1 === endPos || 
            initialPos + 7 === endPos || 
            initialPos + 8 === endPos || 
            initialPos + 9 === endPos || 
            initialPos - 9 === endPos || 
            initialPos - 8 === endPos || 
            initialPos - 7 === endPos 
        );
    }

    getPathIndicies = (initialPos, endPos) => {
        return[];
    }
}
