import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { socket } from '../socket/socket';
import ScrollToBottom from 'react-scroll-to-bottom';
import Message from './Message';

export default function Chat(props) {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");


    useEffect(() => {
        socket.on('message', (receivedMessage) => {
            setMessages([...messages, receivedMessage]);
        })
    }, [messages]);

    //sending messages

    const sendMessage = (event) => {
        event.preventDefault();

        if (message) {
            socket.emit("sendMessage", message, props.gameId, props.username, () => setMessage("")); 
        }
    }

    console.log(message, messages);

    return (
        <div>
            <div>
                <ScrollToBottom>
                    {messages.map((message, i) => (<div key={i}><Message message={message} name={props.username}/></div>))}
                </ScrollToBottom>
            </div>
            <form>
            <TextField 
                id="standard-basic" 
                style={{width: '15vw'}} 
                label="Standard" 
                value={message} 
                onChange={(event) => setMessage(event.target.value)} 
                onKeyPress={event => event.key === 'Enter' ? sendMessage(event) : null}
            />
            <Button variant="contained" color="primary" onClick={(event) => sendMessage(event)}>
                Send
            </Button>
            </form>
        </div>
    )
}
